package learnig.com;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 1;
		switch (day) {
			case 1:
				System.out.println("monday");
				break;
			case 2:
				System.out.println("Tuesday");
				break;
			default:
				System.out.println("no day found");
		
		}
	}

}
